async function loadComponent(e,t){try{const o=await fetch(t);if(!o.ok)return;const n=await o.text(),r=document.getElementById(e);if(r){r.innerHTML=n;r.querySelectorAll("script").forEach(e=>{const t=document.createElement("script");e.src?t.src=e.src:t.textContent=e.textContent,e.parentNode.replaceChild(t,e)})}}catch(e){console.error(`Failed to load component ${t}:`,e)}}Promise.all([loadComponent("header-placeholder","/_header.html"),loadComponent("footer-placeholder","/_footer.html")]);